DROP TABLE IF EXISTS `blog_class`;
CREATE TABLE `blog_class`  (
  `class_id` int(11) NOT NULL AUTO_INCREMENT,
  `class_name` varchar(6) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `class_describe` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL DEFAULT NULL,
  PRIMARY KEY (`class_id`) USING BTREE
) ENGINE = MyISAM AUTO_INCREMENT = 17 CHARACTER SET = utf8 COLLATE = utf8_unicode_ci ROW_FORMAT = Dynamic;

