-- ----------------------------
-- Table structure for blog_comments
-- ----------------------------
DROP TABLE IF EXISTS `blog_comments`;
CREATE TABLE `blog_comments`  (
  `comments_id` int(11) NOT NULL AUTO_INCREMENT,
  `comments_text` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `comments_ids` int(11) NULL DEFAULT NULL,
  `comments_author` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `comments_time` datetime NOT NULL,
  `article_id` int(11) NOT NULL,
  PRIMARY KEY (`comments_id`) USING BTREE
