-- ----------------------------
-- View structure for gettimes
-- ----------------------------
DROP VIEW IF EXISTS `gettimes`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `gettimes` AS select distinct year(`blog_article`.`time`) AS `year` from `blog_article`;

SET FOREIGN_KEY_CHECKS = 1;
